/**
 * TitlesList
 * @summary A component that returns a list of titles
 */

import React from 'react';
import { useState, useEffect } from 'react';

useEffect(() => {
    fetch("/api/getUserDetails", { credentials: 'include' })
        .then(res => {
            if (res.status === 200) {
                return res.json();
            }
        }).then(data => {
            setDetails({ data: data });
            fetch("/api/getUserLists?name=" + data.username)
                .then(res => {
                    if (res.status === 200) {
                        return res.json();
                    }
                }).then(data => {
                    setLists({ data: data });
                });
        })
        .catch(err => {
            console.error(err);
            alert('Error checking for valid token.');
        });
}, []);

const TitlesList = props => {

    return (
        <div>
            <h5>{props.title}</h5>
            <ul style={listStyle}>
                <li style={itemStyle}>The Office</li>
                <li style={itemStyle}>Parks and Rec</li>
                <li style={itemStyle}>Game of Thrones</li>
                <li style={itemStyle}>The Office</li>
                <li style={itemStyle}>Parks and Rec</li>
                <li style={itemStyle}>Game of Thrones</li>
            </ul>
        </div>
    );
}

const listStyle = {
    backgroundColor: '#C0C0C0',
    listStyle: 'none',
    paddingBottom: 1,
    paddingTop: 6,
    paddingLeft: 3,
    paddingRight: 3,
    borderRadius: 5
}

const itemStyle = {
    backgroundColor: '#DCDCDC',
    marginBottom: 5,
    paddingLeft: 10,
    borderRadius: 5
}

export default TitlesList;
